import React from "react";
import { motion } from "framer-motion";

export default function Home() {
  return (
    <motion.div
      className="flex flex-col items-center justify-center h-full p-10 text-center"
      initial={{ opacity: 0, y: 40 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <h1 className="text-5xl font-bold mb-4 text-white">
        Welcome to <span className="text-blue-400">Coxdiack Group</span>
      </h1>
      <p className="text-lg text-gray-300 max-w-2xl">
        Your creative digital space â€” where imagination meets innovation.  
        Explore our portfolio, share your feedback, and experience the next level  
        of digital storytelling and modern design.
      </p>

      <motion.button
        className="mt-8 bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-xl font-semibold transition-all"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        Explore Portfolio
      </motion.button>
    </motion.div>
  );
}
