import React from "react";
import { motion } from "framer-motion";

export default function Sidebar({ active, setActive }) {
  const menuItems = [
    { id: "home", label: "Home" },
    { id: "about", label: "About" },
    { id: "services", label: "Services" },
    { id: "portfolio", label: "Portfolio" },
    { id: "feedback", label: "Feedback" },
    { id: "ratings", label: "Ratings" },
    { id: "contact", label: "Contact" },
  ];

  return (
    <motion.aside
      initial={{ x: -250 }}
      animate={{ x: 0 }}
      transition={{ duration: 0.4 }}
      className="fixed left-0 top-0 h-full w-64 bg-[#0A1837] text-white flex flex-col shadow-2xl"
    >
      <div className="p-6 text-2xl font-bold border-b border-gray-600">
        Coxdiack<span className="text-[#00FFA3]">Group</span>
      </div>
      <nav className="flex flex-col mt-6">
        {menuItems.map((item) => (
          <button
            key={item.id}
            onClick={() => setActive(item.id)}
            className={`text-left px-6 py-3 hover:bg-[#132A5A] transition-all ${
              active === item.id ? "bg-[#132A5A]" : ""
            }`}
          >
            {item.label}
          </button>
        ))}
      </nav>
      <div className="mt-auto p-6 text-sm text-gray-400 border-t border-gray-700">
        Â© {new Date().getFullYear()} CoxdiackGroup. All rights reserved.
      </div>
    </motion.aside>
  );
}
