import React from "react";
import { motion } from "framer-motion";
import logo from "../assets/coxdiack-logo.png"; // ✅ Ensure correct path

export default function Home({ setActive }) {
  return (
    <section className="relative p-10 text-center min-h-screen flex flex-col justify-center items-center bg-gradient-to-b from-[#0A1837] to-[#071226] text-white overflow-hidden">
      
      {/* ✅ Animated full-width logo */}
      <motion.div
  initial={{ opacity: 0, y: -50 }}
  animate={{ opacity: 1, y: 0 }}
  transition={{ duration: 1.2, ease: "easeOut" }}
  className="absolute top-0 left-0 w-full"
>
  <img
    src={logo}
    alt="Coxdiack Group Logo"
    className="w-full h-48 object-cover opacity-90"
  />
</motion.div>


      {/* ✅ Welcome Content */}
      <motion.h1
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4, duration: 0.8 }}
        className="text-5xl font-bold mb-4"
      >
        Welcome to <span className="text-[#00FFA3]">Coxdiack Group</span>
      </motion.h1>

      <motion.p
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7, duration: 0.8 }}
        className="text-lg text-gray-300 max-w-2xl mb-8"
      >
        We craft digital experiences and creative solutions for modern brands.
        Explore our portfolio to see what we’ve been up to.
      </motion.p>

      <motion.button
        onClick={() => setActive("portfolio")}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 1, duration: 0.5 }}
        className="bg-[#00FFA3] text-[#071226] px-6 py-3 rounded-lg font-semibold hover:bg-[#00cc82] transition-all"
      >
        Explore Portfolio
      </motion.button>
    </section>
  );
}
