/* CoxdiackGroupApp.jsx */
import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

import Sidebar from "./components/Sidebar";
import Home from "./components/Home";
import About from "./components/About";
import Services from "./components/Services";
import Portfolio from "./components/Portfolio";
import Contact from "./components/Contact";
import Feedback from "./components/Feedback";
import Ratings from "./components/Ratings";
import FloatingWhatsApp from "./components/FloatingWhatsApp";

import "./App.css";

// Animation Variants
const pageVariants = {
  enter: { opacity: 0, x: 50 },
  center: { opacity: 1, x: 0 },
  exit: { opacity: 0, x: -50 },
};

export default function CoxdiackGroupApp() {
  // ✅ Active page state
  const [active, setActive] = useState("home");

  // ✅ Page rendering logic
  const renderPage = () => {
    switch (active) {
      case "home":
        return <Home />;
      case "about":
        return <About />;
      case "services":
        return <Services />;
      case "portfolio":
        return <Portfolio />;
      case "feedback":
        return <Feedback />;
      case "ratings":
        return <Ratings />;
      case "contact":
        return <Contact />;
      default:
        return <Home />;
    }
  };

  // ✅ Main UI
  return (
    <div className="min-h-screen bg-[#071226] text-white font-sans flex">
      {/* Sidebar receives both active + setActive */}
      <Sidebar active={active} setActive={setActive} />

      <main className="flex-1 ml-64 p-8 overflow-hidden">
        <AnimatePresence mode="wait">
          <motion.div
            key={active}
            variants={pageVariants}
            initial="enter"
            animate="center"
            exit="exit"
            transition={{ duration: 0.4 }}
          >
            {renderPage()}
          </motion.div>
        </AnimatePresence>

        <FloatingWhatsApp phone="639166975338" />
      </main>
    </div>
  );
}
